import React from "react";
import styled from "styled-components";
import { COLORS } from "../../Constants";

const STYLES = {
  new: {
    content: "Just Released",
    color: `${COLORS.secondary}`,
  },

  sale: {
    content: "Sale",
    color: `${COLORS.primary}`,
  },

  none: {
    content: undefined,
    color: undefined,
  },
};

const justReleased = Date.now() - 1000 * 60 * 60 * 24 * 4;

const Item = ({ name, imageSrc, price, salePrice, releaseDate, option }) => {
  return (
    <ItemWrapper>
      <Wrapper style={{ "--imageSrc": imageSrc }}>
        <VisibleLabel style={{}}>{releaseDate < justReleased}</VisibleLabel>
      </Wrapper>
      <InfoWrapper>
        <ItemName />
        <ItemPrice />
      </InfoWrapper>
      <InfoWrapper>
        <ItemOptions />
        <ItemNewPrice />
      </InfoWrapper>
    </ItemWrapper>
  );
};

const ItemWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const Wrapper = styled.div`
  content: var(--imageSrc);
  width: 100%;
  height: 100%;
`;

const VisibleLabel = styled.div`
  content: ;
`;

const InfoWrapper = styled.div`
  display: flex;
`;

const ItemName = styled.span``;

const ItemOptions = styled.span``;

const ItemPrice = styled.span``;

const ItemNewPrice = styled(ItemPrice)``;

export default Item;
