import React from "react";
import styled from "styled-components";
import Item from "../Item/Item";
import SHOES from "../../data";

/*
   for (column = 0; column < SHOES.length; column++) {
        for (row = 0; row < 3; row++) {


        }
      } 
      */

const ItemGrid = () => {
  let column = 0;
  let row = 0;
  return (
    <Column>
      {() => {
        for (column = 0; column < SHOES.length; column++) {
          const items = [];
          for (row = 0; row < 3; row++) {
            const item = SHOES[column];
            items.push(
              <Item
                name={item.name}
                imageSrc={item.imageSrc}
                price={item.price}
                salePrice={item.salePrice}
                releaseDate={item.releaseDate}
                option={item.numOfColors}
              ></Item>
            );
          }
        }
        <Rows>{items}</Rows>;
      }}

      {SHOES.map((item) => (
        <Item
          name={item.name}
          imageSrc={item.imageSrc}
          price={item.price}
          salePrice={item.salePrice}
          releaseDate={item.releaseDate}
          option={item.numOfColors}
        ></Item>
      ))}
    </Column>
  );
};

const Column = styled.div`
  display: flex;
  gap: 32px;
  margin-top: 32px;
  margin-left: 208px;
`;

const Rows = styled.div``;

export default ItemGrid;
