import React from "react";
import styled from "styled-components";
import { COLORS } from "../../Constants";

const STYLES = {
  new: {
    content: "Just Released",
    color: `${COLORS.secondary}`,
  },

  sale: {
    content: "Sale",
    color: `${COLORS.primary}`,
  },

  none: {
    content: undefined,
    color: undefined,
  },
};

const toOld = Date.now() - 1000 * 60 * 60 * 24 * 4;

const Item = ({ name, imageSrc, price, salePrice, releaseDate, option }) => {
  let style = undefined;

  if (releaseDate < toOld) {
    style = STYLES.new;
  } else if (salePrice) {
    style = STYLES.sale;
  }

  return (
    <ItemWrapper>
      <Wrapper>
        <Image src={{ imageSrc }}></Image>
        {style && (
          <VisibleLabel
            style={{
              "--color": style.color,
            }}
          >
            {style.content}
          </VisibleLabel>
        )}
      </Wrapper>
      <InfoWrapper>
        <ItemName>{name}</ItemName>
        <ItemPrice />
      </InfoWrapper>
      <InfoWrapper>
        <ItemOptions />
        <ItemNewPrice />
      </InfoWrapper>
    </ItemWrapper>
  );
};

const ItemWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const Wrapper = styled.div`
  width: 100%;
  height: auto;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
`;

const VisibleLabel = styled.div`
  content: ;
`;

const InfoWrapper = styled.div`
  display: flex;
`;

const ItemName = styled.span``;

const ItemOptions = styled.span``;

const ItemPrice = styled.span``;

const ItemNewPrice = styled(ItemPrice)``;

export default Item;
