import React from "react";
import styled from "styled-components";
import { COLORS, FONTS, WEIGHTS } from "../../Constants";

const STYLES = {
  new: {
    content: "Just Released",
    color: `${COLORS.secondary}`,
  },

  sale: {
    content: "Sale",
    color: `${COLORS.primary}`,
  },

  none: {
    content: undefined,
    color: undefined,
  },
};

const toOld = Date.now() - 1000 * 60 * 60 * 24 * 3;

const Item = ({ name, imageSrc, price, salePrice, releaseDate, option }) => {
  let style = undefined;

  if (releaseDate > toOld) {
    style = STYLES.new;
  } else if (salePrice) {
    style = STYLES.sale;
  }

  return (
    <ItemWrapper>
      <Wrapper>
        {style && (
          <VisibleLabel
            style={{
              "--color": style.color,
            }}
          >
            {style.content}
          </VisibleLabel>
        )}
        <ItemImage src={imageSrc}></ItemImage>
      </Wrapper>
      <InfoWrapper>
        <ItemName>{name}</ItemName>
        <ItemPrice>{"$" + `${price / 100}`}</ItemPrice>
      </InfoWrapper>
      <InfoWrapper>
        <ItemOptions>
          {option + `${option > 1 ? " Colors" : " Color"}`}
        </ItemOptions>
        {salePrice && <ItemNewPrice>{"$" + `${salePrice / 100}`}</ItemNewPrice>}
      </InfoWrapper>
    </ItemWrapper>
  );
};

const ItemWrapper = styled.div`
  display: flex;
  max-width: 31%;
  flex-direction: column;
  margin-bottom: 62px;
`;

const Wrapper = styled.div`
  radius: 16px, 16px, 4px, 4px;
`;

const ItemImage = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 8px;
`;

const VisibleLabel = styled.div`
  position: absolute;
  right: -16px;
`;

const InfoWrapper = styled.div`
  display: flex;
  justify-content: space-between;
`;

const ItemName = styled.span`
  font-family: ${FONTS.raleway};
  font-size: ${16 / 16}rem;
  font-weight: ${WEIGHTS.medium};
  color: ${COLORS.gray[900]};
`;

const ItemOptions = styled.span``;

const ItemPrice = styled.span``;

const ItemNewPrice = styled(ItemPrice)``;

export default Item;
