import React from "react";
import styled from "styled-components";
import Item from "../Item/Item";
import SHOES from "../../data";

/*

      {SHOES.map((item) => (
        <Item
          name={item.name}
          imageSrc={item.imageSrc}
          price={item.price}
          salePrice={item.salePrice}
          releaseDate={item.releaseDate}
          option={item.numOfColors}
        ></Item>
      ))}
*/

/*
   for (column = 0; column < SHOES.length; column++) {
        for (row = 0; row < 3; row++) {


        }
      } 
      */

/*
          {() => {
        for (column = 0; column < SHOES.length; column++) {
          items = [];
          for (row = 0; row < 3; row++) {
            console.log(`row: ${row} item.name: ${item.name}`);
            const item = SHOES[column];
            items.push(
              <Item
                name={item.name}
                imageSrc={item.imageSrc}
                price={item.price}
                salePrice={item.salePrice}
                releaseDate={item.releaseDate}
                option={item.numOfColors}
              ></Item>
            );
          }
        }
        <Rows>{items}</Rows>;
      }}
      */

const ItemGrid = () => {
  let column = 0;
  let row = 0;
  let items = [];

  return (
    <Column>
      {SHOES.map((item) => {
        row++;
        if (row < 4) {
          items.push(
            <Item
              name={item.name}
              imageSrc={item.imageSrc}
              price={item.price}
              salePrice={item.salePrice}
              releaseDate={item.releaseDate}
              option={item.numOfColors}
            ></Item>
          );
          row = 0;
        }
        <Row>{items}</Row>;
      })}
    </Column>
  );
};

const Column = styled.div`
  display: flex;
  gap: 32px;
  margin-top: 32px;
  margin-left: 208px;
`;

const Row = styled.div``;

export default ItemGrid;
